import "dotenv/config";import cors from "cors";import express from "express";import { toNodeHandler } from "better-auth/node";import { auth } from "./auth.js";const app = express();const port = Number(process.env.PORT ?? 3000);app.use(cors({    origin: (origin, callback) => {        const trusted = (process.env.TRUSTED_ORIGINS ?? "")            .split(",")            .map((item) => item.trim())            .filter((item) => item.length > 0);        if (!origin || trusted.includes(origin)) {            return callback(null, true);        }        return callback(new Error("Origin not allowed by CORS"));    },    credentials: true}));app.all("/api/auth/{*any}", toNodeHandler(auth));app.get("/health", (_req, res) => {    res.json({ status: "ok" });});app.listen(port, () => {    console.log(`Better Auth server listening on http://localhost:${port}`);});